            {% for category, message in messages %}
                <div class="alert alert-{{ category }}">
                    {{ message }}
                </div>
            {% endfor %}
        {% endif %}
    {% endwith %}

    <form id="addClassForm" method="POST" action="{{ url_for('them_lh') }}">
        <div class="form-group">
            <label for="classId" class="required">M√£ l·ªõp h·ªçc</label>
            <input type="text" id="classId" name="classId" class="form-control" value="{{ next_id }}" readonly>
            <small style="color: #666; font-style: italic;">M√£ l·ªõp h·ªçc ƒë∆∞·ª£c t·∫°o t·ª± ƒë·ªông</small>
        </div>

        <div class="form-row">
            <div class="form-group">
                <label for="subject" class="required">M√¥n h·ªçc</label>
                <select id="subject" name="subject" class="form-control" required>
                    <option value="">-- Ch·ªçn m√¥n h·ªçc --</option>
                    <option value="To√°n">To√°n</option>
                    <option value="Ng·ªØ VƒÉn">Ng·ªØ VƒÉn</option>
                    <option value="Ti·∫øng Anh">Ti·∫øng Anh</option>
                    <option value="V·∫≠t L√Ω">V·∫≠t L√Ω</option>
                    <option value="H√≥a H·ªçc">H√≥a H·ªçc</option>
                    <option value="Sinh H·ªçc">Sinh H·ªçc</option>
                    <option value="L·ªãch S·ª≠">L·ªãch S·ª≠</option>
                    <option value="ƒê·ªãa L√Ω">ƒê·ªãa L√Ω</option>
                </select>
            </div>

            <div class="form-group">
                <label for="grade" class="required">C·∫•p h·ªçc</label>
                <select id="grade" name="grade" class="form-control" required>
                    <option value="">-- Ch·ªçn c·∫•p h·ªçc --</option>
                    <option value="L·ªõp 1">L·ªõp 1</option>
                    <option value="L·ªõp 2">L·ªõp 2</option>
                    <option value="L·ªõp 3">L·ªõp 3</option>
                    <option value="L·ªõp 4">L·ªõp 4</option>
                    <option value="L·ªõp 5">L·ªõp 5</option>
                    <option value="L·ªõp 6">L·ªõp 6</option>
                    <option value="L·ªõp 7">L·ªõp 7</option>
                    <option value="L·ªõp 8">L·ªõp 8</option>
                    <option value="L·ªõp 9">L·ªõp 9</option>
                    <option value="L·ªõp 10">L·ªõp 10</option>
                    <option value="L·ªõp 11">L·ªõp 11</option>
                    <option value="L·ªõp 12">L·ªõp 12</option>
                </select>
            </div>
        </div>

        <div class="form-row">
            <div class="form-group">
                <label for="schedule" class="required">L·ªãch h·ªçc</label>
                <input type="date" id="schedule" name="schedule" class="form-control" required>
            </div>

            <div class="form-group">
                <label for="location" class="required">ƒê·ªãa ƒëi·ªÉm</label>
                <select id="location" name="location" class="form-control" required>
                    <option value="">-- Ch·ªçn ƒë·ªãa ƒëi·ªÉm --</option>
                    <option value="Online">Online</option>
                    <option value="T·∫°i nh√† ph·ª• huynh">T·∫°i nh√† ph·ª• huynh</option>
                    <option value="T·∫°i trung t√¢m">T·∫°i trung t√¢m</option>
                </select>
            </div>
        </div>

        <div class="form-group">
            <label for="transactionId" class="required">M√£ giao d·ªãch</label>
            <select id="transactionId" name="transactionId" class="form-control" required>
                <option value="">-- Ch·ªçn m√£ giao d·ªãch --</option>
                {% for gd in ds_gd %}
                <option value="{{ gd.MaGD }}">{{ gd.MaGD }} - PH: {{ gd.MaPH }} - GS: {{ gd.MaGS }}</option>
                {% endfor %}
            </select>
        </div>

        <div class="btn-container">
            <button type="button" class="btn btn-secondary" onclick="window.history.back()">H·ªßy b·ªè</button>
            <button type="submit" class="btn btn-primary">L∆∞u th√¥ng tin</button>
        </div>
    </form>
</div>
{% endblock %}

{% block extra_css %}
<style>
    .form-group {
        margin-bottom: 20px;
    }

    .form-group label {
        display: block;
        margin-bottom: 8px;
        font-weight: 600;
        color: var(--text-dark);
    }

    .form-group label.required:after {
        content: " *";
        color: #e53e3e;
    }

    .form-control {
        width: 100%;
        padding: 10px 12px;
        border: 1px solid #e2e8f0;
        border-radius: 4px;
        font-size: 16px;
        transition: border-color 0.3s;
    }

    .form-control:focus {
        border-color: var(--primary-color);
        outline: none;
        box-shadow: 0 0 0 3px rgba(66, 153, 225, 0.2);
    }

    .form-row {
        display: flex;
        gap: 20px;
        margin-bottom: 0;
    }

    .form-row .form-group {
        flex: 1;
    }

    .btn-container {
        display: flex;
        justify-content: flex-end;
        gap: 15px;
        margin-top: 30px;
    }

    .alert {
        padding: 12px 15px;
        margin-bottom: 20px;
        border-radius: 4px;
        font-weight: 500;
    }

    .alert-danger {
        background-color: #fee2e2;
        color: #b91c1c;
        border: 1px solid #f87171;
    }

    .alert-success {
        background-color: #dcfce7;
        color: #166534;
        border: 1px solid #86efac;
    }

    .alert-info {
        background-color: #dbeafe;
        color: #1e40af;
        border: 1px solid #93c5fd;
    }

    @media (max-width: 768px) {
        .form-row {
            flex-direction: column;
            gap: 0;
        }
    }
</style>
{% endblock %}

{% block extra_js %}
<script>
    document.addEventListener('DOMContentLoaded', function() {
        // Set today as the default date for schedule
        const today = new Date().toISOString().split('T')[0];
        document.getElementById('schedule').value = today;

        // Form validation
        const form = document.getElementById('addClassForm');
        form.addEventListener('submit', function(event) {
            const subject = document.getElementById('subject').value;
            const grade = document.getElementById('grade').value;
            const schedule = document.getElementById('schedule').value;
            const location = document.getElementById('location').value;
            const transactionId = document.getElementById('transactionId').value;

            if (!subject || !grade || !schedule || !location || !transactionId) {
                event.preventDefault();
                alert('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin b·∫Øt bu·ªôc!');
                return false;
            }

            // Form is valid, continue with submission
            console.log('Form submitted with data:', {
                classId: document.getElementById('classId').value,
                subject: subject,
                grade: grade,
                schedule: schedule,
                location: location,
                transactionId: transactionId
            });

            // Disable the submit button to prevent double submission
            document.querySelector('button[type="submit"]').disabled = true;
            document.querySelector('button[type="submit"]').textContent = 'ƒêang x·ª≠ l√Ω...';

            // Submit the form
            return true;
        });
    });
</script>
{% endblock %}

